            public static int getFinalFloor()
            {
                var floor = 0;
                foreach (var character in input)
                {
                    if (character.Equals('('))
                    {
                        floor++;
                    }
                    else if (character.Equals(')'))
                    {
                        floor--;
                    }
                    
                }
                return floor;
            }

            public static int getFirstEnterOfBasement()
            {
            var floor = 0;
            var counter = 0;
                foreach (var character in input)
                {
                    
                    if (character.Equals('('))
                    {
                        floor++;
                        counter++;
                    }
                    else if (character.Equals(')'))
                    {
                        floor--;
                        counter++;
                    }
                    if (floor == -1)
                    {
                        return counter;
                    }

                }
                return -1;
            }



